sys_script_include.script:
var MapManager = Class.create();
MapManager.prototype = {
    initialize: function() {
    },

	getMaps: function(){
		var array = [];
		var gr = new GlideRecord("sys_properties");
		gr.addQuery("description", "PayloadMap");
		gr.query();
		while(gr.next()) {
			var obj = {
				suffix: gr.suffix + '',
				name: gr.name + '',
				value: gr.value + ''
			};
			array.push(obj);
		}
		return array;
	},

	saveMap: function(mapName, map){
		if(mapName && map){
			var mapString = JSON.stringify(map);
			gs.setProperty(mapName, mapString);
			return true;
		}else{
			return false;
		}
	},

    type: 'MapManager'
};
