sys_script_include.script:
var PreProcessor = Class.create();
PreProcessor.prototype = {
    initialize: function() {
    },

	process: function(obj){

		return obj;
	},

	combineArrayData: function(array){
		var unitedObj = {};
		for(var i = 0; i < array.length; i++){
			var obj = array[i];
			this.addObjToUnitedObj(unitedObj, obj);
		}
		return unitedObj;
	},

	addObjToUnitedObj: function(unitedObj, obj){
		for(var key in obj){
			var val = obj[key];
			if(typeof val === "object"){
				if(Array.isArray(val)){
					if(!unitedObj[key]){
						unitedObj[key] = [];
					}
					this.addArrayToUnitedArray(unitedObj[key], val);
				}else{
					if(!unitedObj[key]){
						unitedObj[key] = {};
					}
					this.addObjToUnitedObj(unitedObj[key], val);
				}
			}else{
				if(!unitedObj[key]){
					unitedObj[key] = val;
				}
			}
		}
	},


	addArrayToUnitedArray: function(unitedArray, array){
		for(var i = 0; i < array.length; i++){
			var val = array[i];
			if(typeof val === "object"){
				if(!unitedArray[i]){
					unitedArray[i] = {};
					this.addObjToUnitedObj(unitedArray[i], val);
				}
			}else{
				if(!unitedArray[i]){
					unitedArray[i] = val;
				}
			}
		}
	},
    type: 'PreProcessor'
};
